package Basic;

public class DoWhilleLoop {
	// Also known as post test loop in this first statement execute then check the condition , in this at least
	// one time statement execute if statement is false.

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=1;
		
		do{
			System.out.println(n+" ");
			++n;
		}
		while(n<=10);

	}

}
